import React from 'react'
import styled from 'styled-components';

function InputForm() {
    return (
        <InfoContainer lightBg={lightBg} id={id}>
            <InfoWrapper>
                <InfoRow imgStart={imgStart}>
                    <Column1>
                        <TextWrapper>
                            <TopLine>{topLine}</TopLine>
                            <Heading lightText={lightTextDesc}>{headline}</Heading>
                            <Subtitle darkText={darkText}>{description}</Subtitle>
                            <BtnWrap>
                                <Button to={buttonTo}
                                smooth={true} 
                                duration={500} 
                                spy={true} 
                                exact={true} 
                                offset={-80} 
                                primary={primary ? 1 : 0}
                                dark={dark ? 1 : 0}
                                dark2={dark2 ? 1: 0}
                                >{buttonLabel}</Button>
                            </BtnWrap>
                        </TextWrapper>
                    </Column1>
                    <Column2>
                        <ImgWrap>
                            <Img src={img} alt={alt}/>
                        </ImgWrap>
                    </Column2>
                </InfoRow>
            </InfoWrapper>
        </InfoContainer>
      );
}

const FormContainer = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    height: calc(100vh-80px);
    background: #000;
`

const StyledContactUs = styled.div`
  display: flex;
  margin: 7rem 5rem;
  padding: 0 3rem;
  gap: 5rem;
  /* font-family: "Space Mono", monospace; */
  font-family: "Noto Sans", sans-serif;
  color: #a6a9b6;
  @media (max-width: 768px) {
    display: block;
    margin: 4rem 0;
    width: 100%;
    padding: 0 1rem;
  }
`;

const StyledLeft = styled.div`
  width: 50%;
  h2 {
    margin-top: 4rem;
    margin-bottom: 2rem;
    font-family: "Montserrat", sans-serif;
    color: #f1f7e7;
    font-size: 2.5rem;
  }
  h4 {
    text-transform: uppercase;
    letter-spacing: 0.1rem;
  }
  p {
    width: 90%;
  }
  @media (max-width: 768px) {
    width: 90%;
    margin: 4rem 0;
    h2 {
      font-size: 2rem;
      line-height: 3rem;
    }
  }
`;

const StyledForm = styled.div`
  background-color: #fdf6f0;
  border-radius: 10px;
  width: 50%;
  height: auto;
  padding: 3.5rem 3rem;
  color: #17223b;
