    input {
      border: 1px solid #9d9d9d;
      border-radius: 10px;
      outline: none;
      background: transparent;
      height: 50px;
      padding: 0 15px;
      width: 100%;
      font-family: "PT Mono", monospace;
      color: #17223b;
      ::placeholder {
        color: #17223b;
      }
    }
  }
  .textarea {
    background-color: transparent;
    border: 1px solid #9d9d9d;
    border-radius: 10px;
    width: 100%;
    outline: none;
    font-family: "PT Mono", monospace;
    color: #17223b;
    padding: 0.8rem 1rem;
    ::placeholder {
      color: #17223b;
    }
  }
  .f-button {
    width: 100%;
    background-color: #17223b;
    text-align: center;
    padding: 1.2rem 2.5rem;
    color: white;
    cursor: pointer;
    border-radius: 10px;
    display: inline-block;
    font-size: inherit;
    text-transform: uppercase;
    position: relative;
    transition: all 0.4s ease-in-out;
    input {
      font-family: "PT Mono", monospace;
      background-color: transparent;
      color: #fdf6f0;
      border: none;
      font-size: 0.9rem;
    }
    &::after {
      content: "";
      position: absolute;
      width: 0;
      height: 0.2rem;
      transition: all 0.4s ease-in-out;
      left: 0;
      bottom: 0;
      opacity: 0.7;
    }
    &:hover::after {
      width: 100%;
      background-color: var(--white-color);
    }
  }
`;

